import React, { useEffect } from "react";
import api from "./api/axios";

function App() {
  useEffect(() => {
    const baseURL =
      process.env.NODE_ENV === "development"
        ? "http://localhost:8800/api/hotels" // Local backend
        : "https://mern-booking-backend-sov6.onrender.com/api/hotels"; // Production backend
    api.defaults.baseURL = baseURL; // Dynamically set baseURL

    api
      .get("/?min=1&max=999")
      .then((response) => console.log("API Response:", response.data))
      .catch((error) => console.error("API Error:", error.message));
  }, []);

  return (
    <div>
      Check browser console (F12) for API response or error!
      <p>App is loading... (Env: {process.env.NODE_ENV})</p>
    </div>
  );
}

export default App;
