import React, { useEffect, useState } from "react";
import api from "./api/axios";

function App() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hotels, setHotels] = useState([]);

  useEffect(() => {
    const baseURL =
      process.env.NODE_ENV === "development"
        ? "http://localhost:8800/api/hotels"
        : "https://mern-booking-backend-sov6.onrender.com/api/hotels";
    api.defaults.baseURL = baseURL;

    api
      .get("/?min=1&max=999")
      .then((response) => {
        console.log("API Response:", response.data);
        setHotels(response.data); // Ensure this updates the state
        setLoading(false);
      })
      .catch((error) => {
        console.error("API Error:", error.message);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  console.log("Hotels state:", hotels); // Debug log

  return (
    <div>
      <h1>Hotel List</h1>
      {hotels.length > 0 ? (
        <ul>
          {hotels.map((hotel) => (
            <li key={hotel._id}>{hotel.name || "No name"}</li>
          ))}
        </ul>
      ) : (
        <p>No hotels found. Check the console (F12) for API response.</p>
      )}
      <p>App is loaded! (Env: {process.env.NODE_ENV})</p>
    </div>
  );
}

export default App;
