import React, { useEffect, useState } from "react";
import api from "../../api/axios"; // Adjust path based on your structure
import "./Home.css"; // Create this CSS file

function Home() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hotels, setHotels] = useState([]);

  useEffect(() => {
    const baseURL =
      process.env.NODE_ENV === "development"
        ? "http://localhost:8800/api/hotels"
        : "https://mern-booking-backend-sov6.vercel.app/api/hotels";
    api.defaults.baseURL = baseURL;

    api
      .get("/?min=1&max=999")
      .then((response) => {
        console.log("API Response:", response.data);
        setHotels(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("API Error:", error.message);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  console.log("Hotels state:", hotels);

  return (
    <div className="home">
      <header className="header">
        <h1>MAX GRAND SUITES</h1>
        <nav>
          <button>Stays</button>
          <button>Flights</button>
          <button>Car rentals</button>
          <button>Attractions</button>
          <button>Airport taxis</button>
        </nav>
        <div className="auth-buttons">
          <button>Register</button>
          <button>Login</button>
        </div>
      </header>
      <main>
        <section className="genius-section">
          <h2>A lifetime of discounts? It’s Genius.</h2>
          <p>
            Get rewarded for your travels – unlock instant savings of 10% or more
            with a free booking account
          </p>
          <button>Sign in / Register</button>
        </section>
        <section className="search-section">
          <input type="text" placeholder="Where are you going?" />
          <input type="date" />
          <input type="date" />
          <select>
            <option>1 adult · 0 children · 1 room</option>
          </select>
          <button>Search</button>
        </section>
        <section className="featured-properties">
          <h2>Featured Properties</h2>
          {hotels.length > 0 ? (
            <div className="property-cards">
              {hotels.map((hotel) => (
                <div key={hotel._id} className="property-card">
                  <img src="https://via.placeholder.com/200" alt={hotel.name} />
                  <h3>{hotel.city}</h3>
                  <p>{hotel.name} - 1 properties</p>
                </div>
              ))}
            </div>
          ) : (
            <p>No properties found.</p>
          )}
        </section>
      </main>
    </div>
  );
}

export default Home;
